import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import io from 'socket.io-client';
import Plot from 'react-plotly.js';
// import './Realtime.css'; // Uncomment this if you have a Realtime.css file

const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:5000/api';
const SOCKET_URL = process.env.REACT_APP_SOCKET_URL || 'http://localhost:5000';

// Hook names mapping
const HOOK_NAMES = {
  0: 'PRE_ROUTING',
  1: 'LOCAL_IN',
  2: 'FORWARD',
  3: 'LOCAL_OUT',
  4: 'POST_ROUTING'
};

// Hook order for visualization
const HOOK_ORDER = ['PRE_ROUTING', 'LOCAL_IN', 'FORWARD', 'LOCAL_OUT', 'POST_ROUTING'];
const LAYER_ORDER = ['Ingress', 'L2', 'IP', 'Firewall', 'Socket', 'Egress'];

const HOOK_COLORS = {
  'PRE_ROUTING': '#667eea',
  'LOCAL_IN': '#28a745',
  'FORWARD': '#ffc107',
  'LOCAL_OUT': '#dc3545',
  'POST_ROUTING': '#6c757d',
  'PREROUTING': '#667eea',
  'INPUT': '#28a745',
  'OUTPUT': '#dc3545',
  'POSTROUTING': '#6c757d'
};

const VERDICT_COLORS = {
  'ACCEPT': '#28a745',
  'DROP': '#dc3545',
  'QUEUE': '#ffc107',
  'STOLEN': '#6c757d',
  'CONTINUE': '#17a2b8'
};

const PROTOCOL_ICONS = {
  'TCP': 'üî∑',
  'UDP': 'üî∂',
  'ICMP': 'üì°',
  'PROTO_6': 'üî∑',
  'PROTO_17': 'üî∂',
  'PROTO_1': 'üì°'
};

// Color based on drop rate
function getColorFromDropRate(dropRate) {
  if (dropRate <= 5) {
    return 'rgba(0, 255, 127, 0.8)'; // Green
  } else if (dropRate <= 10) {
    return 'rgba(144, 238, 144, 0.8)'; // Light Green
  } else if (dropRate <= 20) {
    return 'rgba(255, 215, 0, 0.8)'; // Gold
  } else if (dropRate <= 50) {
    return 'rgba(255, 165, 0, 0.8)'; // Orange
  } else {
    return 'rgba(255, 0, 0, 0.8)'; // Red
  }
}

// Build Sankey diagram data
function buildSankeyData(stats) {
  if (!stats || !stats.hooks) {
    return { nodes: [], links: [] };
  }

  const nodes = [];
  const links = [];
  const nodeMap = new Map(); // name -> index
  let nodeIndex = 0;

  // 1. Add Hook nodes
  HOOK_ORDER.forEach(hookName => {
    if (stats.hooks[hookName]) {
      const hookData = stats.hooks[hookName];
      nodes.push({
        label: `<b>${hookName}</b><br>${hookData.packets_total} pkts`,
        color: 'rgba(100, 150, 255, 0.8)',
        customdata: { type: 'hook', name: hookName }
      });
      nodeMap.set(hookName, nodeIndex++);
    }
  });

  // 2. Add Layer nodes (for each hook)
  HOOK_ORDER.forEach(hookName => {
    const hookData = stats.hooks[hookName];
    if (!hookData) return;

    const layers = hookData.layers || {};
    LAYER_ORDER.forEach(layerName => {
      if (layers[layerName]) {
        const layerData = layers[layerName];
        const nodeName = `${hookName}::${layerName}`;

        // Color based on drop rate
        const color = getColorFromDropRate(layerData.drop_rate || 0);

        nodes.push({
          label: `<b>${layerName}</b><br>${layerData.packets_in} pkts<br>${layerData.drop_rate?.toFixed(1) || 0}% drop`,
          color: color,
          customdata: {
            type: 'layer',
            hook: hookName,
            layer: layerName,
            data: layerData
          }
        });
        nodeMap.set(nodeName, nodeIndex++);
      }
    });
  });

  // 3. Build links: Hook -> Layer
  HOOK_ORDER.forEach(hookName => {
    const hookData = stats.hooks[hookName];
    if (!hookData) return;

    const hookIndex = nodeMap.get(hookName);
    const layers = hookData.layers || {};

    LAYER_ORDER.forEach(layerName => {
      if (layers[layerName]) {
        const layerData = layers[layerName];
        const layerNodeName = `${hookName}::${layerName}`;
        const layerIndex = nodeMap.get(layerNodeName);

        // Link value = packets_in
        links.push({
          source: hookIndex,
          target: layerIndex,
          value: layerData.packets_in,
          color: 'rgba(200, 200, 200, 0.3)',
          customdata: {
            hook: hookName,
            layer: layerName,
            packets: layerData.packets_in,
            drops: layerData.drops || 0
          }
        });
      }
    });
  });

  return { nodes, links };
}

function RealtimeView() {
  const [enabled, setEnabled] = useState(false);
  const [connected, setConnected] = useState(false);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [sankeyData, setSankeyData] = useState(null);

  const socketRef = useRef(null);
  // Removed: recentEvents and maxEvents - no longer needed

  // Initialize Socket.IO connection
  useEffect(() => {
    const socket = io(SOCKET_URL, {
      transports: ['websocket', 'polling'],
      reconnection: true,
      reconnectionDelay: 1000,
      reconnectionAttempts: 5
    });

    socket.on('connect', () => {
      console.log('[Realtime] Connected to WebSocket');
      setConnected(true);
      loadStats();
    });

    socket.on('disconnect', () => {
      console.log('[Realtime] Disconnected from WebSocket');
      setConnected(false);
    });

    // Removed packet_event listener - only use stats_update now
    // socket.on('packet_event', ...) 
    // We now rely on stats_update every 1s for visualization

    socket.on('stats_update', (stats) => {
      console.log('[Realtime] Received stats_update:', stats);
      setStats(stats);

      // Build Sankey data
      const { nodes, links } = buildSankeyData(stats);
      if (nodes.length > 0) {
        setSankeyData({ nodes, links });
      }
    });

    socket.on('status', (status) => {
      setEnabled(status.enabled);
    });

    socketRef.current = socket;

    return () => {
      socket.disconnect();
    };
  }, []);

  // Load stats periodically - every 1 second to match backend emission
  useEffect(() => {
    if (enabled && connected) {
      const interval = setInterval(loadStats, 1000);
      return () => clearInterval(interval);
    }
  }, [enabled, connected]);

  const loadStats = async () => {
    try {
      const res = await axios.get(`${API_BASE}/realtime/stats`);
      setStats(res.data);
      setEnabled(res.data.enabled);

      // Build Sankey data
      if (res.data && res.data.hooks) {
        const { nodes, links } = buildSankeyData(res.data);
        if (nodes.length > 0) {
          setSankeyData({ nodes, links });
        }
      }
    } catch (err) {
      console.error('Error loading realtime stats:', err);
    }
  };

  const toggleRealtime = async () => {
    setLoading(true);
    setError(null);

    try {
      if (enabled) {
        await axios.post(`${API_BASE}/realtime/disable`);
        setEnabled(false);
        setSankeyData(null);
      } else {
        await axios.post(`${API_BASE}/realtime/enable`);
        setEnabled(true);
      }
      await loadStats();
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to toggle realtime');
    } finally {
      setLoading(false);
    }
  };

  const resetStats = async () => {
    setLoading(true);
    try {
      await axios.post(`${API_BASE}/realtime/reset`);
      setSankeyData(null);
      await loadStats();
    } catch (err) {
      setError('Failed to reset stats');
    } finally {
      setLoading(false);
    }
  };

  const formatTimestamp = (timestamp) => {
    const date = new Date(timestamp * 1000);
    return date.toLocaleTimeString('en-US', { 
      hour12: false, 
      hour: '2-digit', 
      minute: '2-digit', 
      second: '2-digit',
      fractionalSecondDigits: 3 
    });
  };

  return (
    <div className="realtime-container">
      {error && (
        <div className="realtime-error">
          <strong>‚ö†Ô∏è Error:</strong> {error}
          <button onClick={() => setError(null)}>‚úï</button>
        </div>
      )}

      {/* Header Controls */}
      <div className="realtime-header">
        <div className="realtime-header-left">
          <h2>üé• Realtime Packet Visualization</h2>
          <div className="connection-status">
            <span className={`status-dot ${connected ? 'connected' : 'disconnected'}`}></span>
            <span>{connected ? 'Connected' : 'Disconnected'}</span>
          </div>
        </div>

        <div className="realtime-header-right">
          <button
            className={`btn ${enabled ? 'btn-danger' : 'btn-primary'}`}
            onClick={toggleRealtime}
            disabled={loading || !connected}
          >
            {loading ? '‚è≥ Loading...' : enabled ? '‚è∏Ô∏è Disable' : '‚ñ∂Ô∏è Enable'}
          </button>

          {enabled && (
            <button
              className="btn btn-secondary"
              onClick={resetStats}
              disabled={loading}
            >
              üîÑ Reset Stats
            </button>
          )}
        </div>
      </div>

      {!enabled && (
        <div className="realtime-disabled-notice">
          <h3>üì∫ Realtime Visualization Disabled</h3>
          <p>Click "Enable" to start capturing live packet events</p>
          <p className="hint">
            üí° Tip: Enable realtime to see live packet flows through hooks and layers
          </p>
        </div>
      )}

      {enabled && stats && (
        <>
          {/* Statistics Dashboard */}
          <div className="realtime-stats-grid">
            <div className="realtime-stat-card primary">
              <div className="stat-icon">üìä</div>
              <div className="stat-content">
                <div className="stat-value">{stats.total_packets?.toLocaleString() || 0}</div>
                <div className="stat-label">Total Packets</div>
              </div>
            </div>

            <div className="realtime-stat-card success">
              <div className="stat-icon">‚ö°</div>
              <div className="stat-content">
                <div className="stat-value">{stats.packets_per_second?.toFixed(1) || 0}</div>
                <div className="stat-label">Packets/Second</div>
              </div>
            </div>

            <div className="realtime-stat-card warning">
              <div className="stat-icon">‚è±Ô∏è</div>
              <div className="stat-content">
                <div className="stat-value">{stats.uptime_seconds?.toFixed(0) || 0}s</div>
                <div className="stat-label">Uptime</div>
              </div>
            </div>

            <div className="realtime-stat-card info">
              <div className="stat-icon">ü™ù</div>
              <div className="stat-content">
                <div className="stat-value">{Object.keys(stats.hooks || {}).length}</div>
                <div className="stat-label">Active Hooks</div>
              </div>
            </div>
          </div>

          {/* 2-LEVEL SANKEY DIAGRAM: Hook ‚Üí Layer */}
          {sankeyData && sankeyData.nodes.length > 0 && (
            <div className="realtime-panel full-width graph-panel">
              <h3>üîÑ Packet Flow: Hook Pipeline ‚Üí Network Layers</h3>
              <div className="graph-legend">
                <div className="legend-item">
                  <div className="legend-color" style={{ background: 'linear-gradient(90deg, #00ff7f, #90EE90)' }}></div>
                  <span>0-5% drop (Excellent)</span>
                </div>
                <div className="legend-item">
                  <div className="legend-color" style={{ background: 'linear-gradient(90deg, #90EE90, #FFD700)' }}></div>
                  <span>5-10% drop (Good)</span>
                </div>
                <div className="legend-item">
                  <div className="legend-color" style={{ background: 'linear-gradient(90deg, #FFD700, #FFA500)' }}></div>
                  <span>10-20% drop (Warning)</span>
                </div>
                <div className="legend-item">
                  <div className="legend-color" style={{ background: 'linear-gradient(90deg, #FFA500, #FF6347)' }}></div>
                  <span>20-50% drop (Critical)</span>
                </div>
                <div className="legend-item">
                  <div className="legend-color" style={{ background: 'linear-gradient(90deg, #FF6347, #FF0000)' }}></div>
                  <span>&gt;50% drop (Severe)</span>
                </div>
              </div>

              <Plot
                data={[
                  {
                    type: 'sankey',
                    orientation: 'h',
                    node: {
                      pad: 15,
                      thickness: 30,
                      line: {
                        color: 'rgba(255, 255, 255, 0.5)',
                        width: 2
                      },
                      label: sankeyData.nodes.map(n => n.label),
                      color: sankeyData.nodes.map(n => n.color),
                      customdata: sankeyData.nodes.map(n => n.customdata)
                    },
                    link: {
                      source: sankeyData.links.map(l => l.source),
                      target: sankeyData.links.map(l => l.target),
                      value: sankeyData.links.map(l => l.value),
                      color: sankeyData.links.map(l => l.color),
                      customdata: sankeyData.links.map(l => l.customdata)
                    }
                  }
                ]}
                layout={{
                  font: { size: 11 },
                  plot_bgcolor: 'rgba(0, 0, 0, 0.02)',
                  paper_bgcolor: 'transparent',
                  margin: { l: 10, r: 10, t: 10, b: 10 },
                  height: 500
                }}
                config={{
                  responsive: true,
                  displayModeBar: false
                }}
                style={{ width: '100%', height: '500px' }}
              />
            </div>
          )}

          {/* Visualization Panels */}
          <div className="realtime-panels">
            {/* Hook Distribution */}
            {stats.hooks && Object.keys(stats.hooks).length > 0 && (
              <div className="realtime-panel">
                <h3>ü™ù Hook Distribution</h3>
                <div className="hook-bars">
                  {Object.entries(stats.hooks).map(([hook, hookData]) => {
                    const maxCount = Math.max(...Object.values(stats.hooks).map(h => h.packets_total || 0));
                    const percentage = maxCount > 0 ? ((hookData.packets_total || 0) / maxCount) * 100 : 0;
                    return (
                      <div key={hook} className="hook-bar-item">
                        <div className="hook-bar-label">
                          <span>{hook}</span>
                          <span className="hook-bar-count">{hookData.packets_total || 0}</span>
                        </div>
                        <div className="hook-bar">
                          <div 
                            className="hook-bar-fill" 
                            style={{ 
                              width: `${percentage}%`,
                              background: HOOK_COLORS[hook] || '#667eea'
                            }}
                          />
                        </div>
                      </div>
                    );
                  })}
                </div>
              </div>
            )}

            {/* Layer Statistics */}
            {stats.hooks && Object.keys(stats.hooks).length > 0 && (
              <div className="realtime-panel">
                <h3>üìä Layer Statistics</h3>
                <div className="layer-stats">
                  {Object.entries(stats.hooks).map(([hookName, hookData]) => {
                    const layers = hookData.layers || {};
                    if (Object.keys(layers).length === 0) return null;

                    return (
                      <div key={hookName} className="layer-stats-section">
                        <h4>{hookName}</h4>
                        {Object.entries(layers).map(([layerName, layerData]) => (
                          <div key={layerName} className="layer-stat-item">
                            <div className="layer-name">{layerName}</div>
                            <div className="layer-metrics">
                              <span className="metric">
                                üì• {layerData.packets_in || 0} pkts
                              </span>
                              <span className={`metric ${layerData.drop_rate > 10 ? 'metric-warning' : ''}`}>
                                üîª {layerData.drop_rate?.toFixed(1) || 0}%
                              </span>
                              <span className="metric">
