import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:5000/api';

function App() {
  // State
  const [sessions, setSessions] = useState([]);
  const [files, setFiles] = useState([]);
  const [newSession, setNewSession] = useState({
    id: '',
    filter: ''
  });
  const [selectedSession, setSelectedSession] = useState(null);
  const [sessionStats, setSessionStats] = useState(null);
  const [health, setHealth] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Load health status
  useEffect(() => {
    checkHealth();
  }, []);

  // Poll active sessions
  useEffect(() => {
    const interval = setInterval(() => {
      if (sessions.length > 0) {
        loadSessions();
        if (selectedSession) {
          loadSessionStats(selectedSession);
        }
      }
    }, 1000);

    return () => clearInterval(interval);
  }, [sessions, selectedSession]);

  // Load files on mount
  useEffect(() => {
    loadFiles();
  }, []);

  // API Calls
  const checkHealth = async () => {
    try {
      const res = await axios.get(`${API_BASE}/health`);
      setHealth(res.data);
    } catch (err) {
      setError('Backend not reachable');
    }
  };

  const loadSessions = async () => {
    try {
      const res = await axios.get(`${API_BASE}/sessions`);
      setSessions(res.data.sessions);
    } catch (err) {
      console.error('Error loading sessions:', err);
    }
  };

  const loadSessionStats = async (sessionId) => {
    try {
      const res = await axios.get(`${API_BASE}/sessions/${sessionId}/stats`);
      setSessionStats(res.data);
    } catch (err) {
      console.error('Error loading stats:', err);
    }
  };

  const loadFiles = async () => {
    try {
      const res = await axios.get(`${API_BASE}/files`);
      setFiles(res.data.files);
    } catch (err) {
      console.error('Error loading files:', err);
    }
  };

  const startSession = async () => {
    setLoading(true);
    setError(null);

    try {
      const sessionId = newSession.id || `trace_${Date.now()}`;
      await axios.post(`${API_BASE}/sessions`, {
        session_id: sessionId,
        pcap_filter: newSession.filter
      });

      setNewSession({ id: '', filter: '' });
      loadSessions();
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to start session');
    } finally {
      setLoading(false);
    }
  };

  const stopSession = async (sessionId) => {
    setLoading(true);
    setError(null);

    try {
      await axios.delete(`${API_BASE}/sessions/${sessionId}`);
      setSessions(sessions.filter(s => s.session_id !== sessionId));
      setSelectedSession(null);
      setSessionStats(null);
      loadFiles();
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to stop session');
    } finally {
      setLoading(false);
    }
  };

  const downloadFile = (filename) => {
    window.open(`${API_BASE}/download/${filename}`, '_blank');
  };

  const formatBytes = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return Math.round(bytes / Math.pow(k, i) * 100) / 100 + ' ' + sizes[i];
  };

  const formatDuration = (seconds) => {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = Math.floor(seconds % 60);

    if (hours > 0) {
      return `${hours}h ${minutes}m ${secs}s`;
    } else if (minutes > 0) {
      return `${minutes}m ${secs}s`;
    } else {
      return `${secs}s`;
    }
  };

  return (
    <div className="App">
      <header className="header">
        <h1>üîç NFT Packet Tracer</h1>
        <div className="header-info">
          {health && (
            <div className={`status ${health.bcc_available ? 'status-ok' : 'status-error'}`}>
              BCC: {health.bcc_available ? '‚úì Available' : '‚úó Not Available'}
            </div>
          )}
        </div>
      </header>

      {error && (
        <div className="error-banner">
          <strong>Error:</strong> {error}
          <button onClick={() => setError(null)}>‚úï</button>
        </div>
      )}

      <div className="container">
        {/* Start New Trace */}
        <section className="card">
          <h2>Start New Trace</h2>
          <div className="form">
            <div className="form-group">
              <label>Session ID (optional)</label>
              <input
                type="text"
                placeholder="Auto-generated if empty"
                value={newSession.id}
                onChange={(e) => setNewSession({ ...newSession, id: e.target.value })}
              />
            </div>
            <div className="form-group">
              <label>PCAP Filter (optional)</label>
              <input
                type="text"
                placeholder="e.g., tcp and dst port 443"
                value={newSession.filter}
                onChange={(e) => setNewSession({ ...newSession, filter: e.target.value })}
              />
              <small>Examples: tcp, udp, port 80, host 192.168.1.1</small>
            </div>
            <button
              className="btn btn-primary"
              onClick={startSession}
              disabled={loading || !health?.bcc_available}
            >
              {loading ? 'Starting...' : '‚ñ∂ Start Trace'}
            </button>
          </div>
        </section>

        {/* Active Sessions */}
        <section className="card">
          <h2>Active Sessions ({sessions.length})</h2>
          {sessions.length === 0 ? (
            <p className="empty-state">No active trace sessions</p>
          ) : (
            <div className="sessions-grid">
              {sessions.map((session) => (
                <div
                  key={session.session_id}
                  className={`session-card ${selectedSession === session.session_id ? 'selected' : ''}`}
                  onClick={() => {
                    setSelectedSession(session.session_id);
                    loadSessionStats(session.session_id);
                  }}
                >
                  <div className="session-header">
                    <h3>{session.session_id}</h3>
                    <span className="badge badge-running">Running</span>
                  </div>
                  <div className="session-stats">
                    <div className="stat">
                      <span className="stat-label">Events/s</span>
                      <span className="stat-value">{session.events_per_second}</span>
                    </div>
                    <div className="stat">
                      <span className="stat-label">Total Events</span>
                      <span className="stat-value">{session.total_events.toLocaleString()}</span>
                    </div>
                    <div className="stat">
                      <span className="stat-label">Active SKBs</span>
                      <span className="stat-value">{session.active_skbs}</span>
                    </div>
                    <div className="stat">
                      <span className="stat-label">Completed</span>
                      <span className="stat-value">{session.completed_skbs}</span>
                    </div>
                    <div className="stat">
                      <span className="stat-label">Uptime</span>
                      <span className="stat-value">{formatDuration(session.uptime_seconds)}</span>
                    </div>
                  </div>
                  <button
                    className="btn btn-danger btn-sm"
                    onClick={(e) => {
                      e.stopPropagation();
                      stopSession(session.session_id);
                    }}
                    disabled={loading}
                  >
                    ‚èπ Stop & Export
                  </button>
                </div>
              ))}
            </div>
          )}
        </section>

        {/* Session Details */}
        {sessionStats && (
          <section className="card">
            <h2>Session Details: {sessionStats.session_id}</h2>
            <div className="details-grid">
              <div className="detail">
                <span className="detail-label">Status</span>
                <span className="detail-value">
                  <span className={`badge ${sessionStats.running ? 'badge-running' : 'badge-stopped'}`}>
                    {sessionStats.running ? 'Running' : 'Stopped'}
                  </span>
                </span>
              </div>
              <div className="detail">
                <span className="detail-label">Start Time</span>
                <span className="detail-value">{new Date(sessionStats.start_time).toLocaleString()}</span>
              </div>
              <div className="detail">
                <span className="detail-label">Uptime</span>
                <span className="detail-value">{formatDuration(sessionStats.uptime_seconds)}</span>
              </div>
              <div className="detail">
                <span className="detail-label">Total Events</span>
                <span className="detail-value">{sessionStats.total_events.toLocaleString()}</span>
              </div>
              <div className="detail">
                <span className="detail-label">Events/Second</span>
                <span className="detail-value">{sessionStats.events_per_second}</span>
              </div>
              <div className="detail">
                <span className="detail-label">Active SKBs</span>
                <span className="detail-value">{sessionStats.active_skbs}</span>
              </div>
              <div className="detail">
                <span className="detail-label">Completed SKBs</span>
                <span className="detail-value">{sessionStats.completed_skbs}</span>
              </div>
            </div>
          </section>
        )}

        {/* Download Files */}
        <section className="card">
          <h2>Trace Results ({files.length})</h2>
          {files.length === 0 ? (
            <p className="empty-state">No trace results available yet</p>
          ) : (
            <div className="files-list">
              {files.map((file) => (
                <div key={file.filename} className="file-item">
                  <div className="file-info">
                    <div className="file-name">üìÑ {file.filename}</div>
                    <div className="file-meta">
                      <span>{formatBytes(file.size)}</span>
                      <span>‚Ä¢</span>
                      <span>{new Date(file.created).toLocaleString()}</span>
                    </div>
                  </div>
                  <button
                    className="btn btn-secondary btn-sm"
                    onClick={() => downloadFile(file.filename)}
                  >
                    ‚¨á Download
                  </button>
                </div>
              ))}
            </div>
          )}
        </section>
      </div>

      <footer className="footer">
        <p>NFT Tracer v1.0 | eBPF-based Network Packet Tracing</p>
      </footer>
    </div>
  );
}

export default App;
