import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import io from 'socket.io-client';
import './Realtime.css';

const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:5000/api';
const SOCKET_URL = process.env.REACT_APP_SOCKET_URL || 'http://localhost:5000';

// Hook names mapping
const HOOK_NAMES = {
  0: 'PREROUTING',
  1: 'INPUT',
  2: 'FORWARD',
  3: 'OUTPUT',
  4: 'POSTROUTING'
};

const HOOK_COLORS = {
  'PREROUTING': '#667eea',
  'INPUT': '#28a745',
  'FORWARD': '#ffc107',
  'OUTPUT': '#dc3545',
  'POSTROUTING': '#6c757d'
};

const VERDICT_COLORS = {
  'ACCEPT': '#28a745',
  'DROP': '#dc3545',
  'QUEUE': '#ffc107',
  'STOLEN': '#6c757d',
  'CONTINUE': '#17a2b8'
};

const PROTOCOL_ICONS = {
  'TCP': 'üî∑',
  'UDP': 'üî∂',
  'ICMP': 'üì°',
  'PROTO_6': 'üî∑',
  'PROTO_17': 'üî∂',
  'PROTO_1': 'üì°'
};

function RealtimeView() {
  const [enabled, setEnabled] = useState(false);
  const [connected, setConnected] = useState(false);
  const [stats, setStats] = useState(null);
  const [recentEvents, setRecentEvents] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const socketRef = useRef(null);
  const maxEvents = 50;

  // Initialize Socket.IO connection
  useEffect(() => {
    const socket = io(SOCKET_URL, {
      transports: ['websocket', 'polling'],
      reconnection: true,
      reconnectionDelay: 1000,
      reconnectionAttempts: 5
    });

    socket.on('connect', () => {
      console.log('[Realtime] Connected to WebSocket');
      setConnected(true);
      loadStats();
    });

    socket.on('disconnect', () => {
      console.log('[Realtime] Disconnected from WebSocket');
      setConnected(false);
    });

    socket.on('packet_event', (event) => {
      setRecentEvents(prev => {
        const newEvents = [event, ...prev].slice(0, maxEvents);
        return newEvents;
      });
    });

    socket.on('stats_update', (stats) => {
      setStats(stats);
    });

    socket.on('status', (status) => {
      setEnabled(status.enabled);
    });

    socketRef.current = socket;

    return () => {
      socket.disconnect();
    };
  }, []);

  // Load stats periodically
  useEffect(() => {
    if (enabled && connected) {
      const interval = setInterval(loadStats, 2000);
      return () => clearInterval(interval);
    }
  }, [enabled, connected]);

  const loadStats = async () => {
    try {
      const res = await axios.get(`${API_BASE}/realtime/stats`);
      setStats(res.data);
      setEnabled(res.data.enabled);
    } catch (err) {
      console.error('Error loading realtime stats:', err);
    }
  };

  const toggleRealtime = async () => {
    setLoading(true);
    setError(null);

    try {
      if (enabled) {
        await axios.post(`${API_BASE}/realtime/disable`);
        setEnabled(false);
        setRecentEvents([]);
      } else {
        await axios.post(`${API_BASE}/realtime/enable`);
        setEnabled(true);
      }
      await loadStats();
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to toggle realtime');
    } finally {
      setLoading(false);
    }
  };

  const resetStats = async () => {
    setLoading(true);
    try {
      await axios.post(`${API_BASE}/realtime/reset`);
      setRecentEvents([]);
      await loadStats();
    } catch (err) {
      setError('Failed to reset stats');
    } finally {
      setLoading(false);
    }
  };

  const formatTimestamp = (timestamp) => {
    const date = new Date(timestamp * 1000);
    return date.toLocaleTimeString('en-US', { 
      hour12: false, 
      hour: '2-digit', 
      minute: '2-digit', 
      second: '2-digit',
      fractionalSecondDigits: 3 
    });
  };

  return (
    <div className="realtime-container">
      {error && (
        <div className="realtime-error">
          <strong>‚ö†Ô∏è Error:</strong> {error}
          <button onClick={() => setError(null)}>‚úï</button>
        </div>
      )}

      {/* Header Controls */}
      <div className="realtime-header">
        <div className="realtime-header-left">
          <h2>üé• Realtime Packet Visualization</h2>
          <div className="connection-status">
            <span className={`status-dot ${connected ? 'connected' : 'disconnected'}`}></span>
            <span>{connected ? 'Connected' : 'Disconnected'}</span>
          </div>
        </div>

        <div className="realtime-header-right">
          <button
            className={`btn ${enabled ? 'btn-danger' : 'btn-primary'}`}
            onClick={toggleRealtime}
            disabled={loading || !connected}
          >
            {loading ? '‚è≥ Loading...' : enabled ? '‚è∏Ô∏è Disable' : '‚ñ∂Ô∏è Enable'}
          </button>

          {enabled && (
            <button
              className="btn btn-secondary"
              onClick={resetStats}
              disabled={loading}
            >
              üîÑ Reset Stats
            </button>
          )}
        </div>
      </div>

      {!enabled && (
        <div className="realtime-disabled-notice">
          <h3>üì∫ Realtime Visualization Disabled</h3>
          <p>Click "Enable" to start capturing live packet events</p>
          <p className="hint">
            üí° Tip: Start a trace session first, then enable realtime to see live packet flows
          </p>
        </div>
      )}

      {enabled && stats && (
        <>
          {/* Statistics Dashboard */}
          <div className="realtime-stats-grid">
            <div className="realtime-stat-card primary">
              <div className="stat-icon">üìä</div>
              <div className="stat-content">
                <div className="stat-value">{stats.total_events?.toLocaleString() || 0}</div>
                <div className="stat-label">Total Events</div>
              </div>
            </div>

            <div className="realtime-stat-card success">
              <div className="stat-icon">‚ö°</div>
              <div className="stat-content">
                <div className="stat-value">{stats.events_per_second || 0}</div>
                <div className="stat-label">Events/Second</div>
              </div>
            </div>

            <div className="realtime-stat-card warning">
              <div className="stat-icon">üîß</div>
              <div className="stat-content">
                <div className="stat-value">{Object.keys(stats.by_function || {}).length}</div>
                <div className="stat-label">Active Functions</div>
              </div>
            </div>

            <div className="realtime-stat-card info">
              <div className="stat-icon">üéØ</div>
              <div className="stat-content">
                <div className="stat-value">{Object.keys(stats.by_hook || {}).length}</div>
                <div className="stat-label">Active Hooks</div>
              </div>
            </div>
          </div>

          {/* Visualization Panels */}
          <div className="realtime-panels">
            {/* Hook Distribution */}
            {stats.by_hook && Object.keys(stats.by_hook).length > 0 && (
              <div className="realtime-panel">
                <h3>ü™ù Hook Distribution</h3>
                <div className="hook-bars">
                  {Object.entries(stats.by_hook).map(([hook, count]) => {
                    const maxCount = Math.max(...Object.values(stats.by_hook));
                    const percentage = (count / maxCount) * 100;
                    return (
                      <div key={hook} className="hook-bar-item">
                        <div className="hook-bar-label">
                          <span>{hook}</span>
                          <span className="hook-bar-count">{count}</span>
                        </div>
                        <div className="hook-bar">
                          <div 
                            className="hook-bar-fill" 
                            style={{ 
                              width: `${percentage}%`,
                              background: HOOK_COLORS[hook] || '#667eea'
                            }}
                          />
                        </div>
                      </div>
                    );
                  })}
                </div>
              </div>
            )}

            {/* Verdict Distribution */}
            {stats.by_verdict && Object.keys(stats.by_verdict).length > 0 && (
              <div className="realtime-panel">
                <h3>‚öñÔ∏è Verdict Distribution</h3>
                <div className="verdict-grid">
                  {Object.entries(stats.by_verdict).map(([verdict, count]) => (
                    <div key={verdict} className="verdict-card">
                      <div 
                        className="verdict-icon"
                        style={{ background: VERDICT_COLORS[verdict] || '#6c757d' }}
                      >
                        {verdict === 'ACCEPT' && '‚úì'}
                        {verdict === 'DROP' && '‚úó'}
                        {verdict === 'QUEUE' && '‚è≥'}
                        {verdict === 'CONTINUE' && '‚Üí'}
                      </div>
                      <div className="verdict-info">
                        <div className="verdict-name">{verdict}</div>
                        <div className="verdict-count">{count}</div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* Protocol Distribution */}
            {stats.by_protocol && Object.keys(stats.by_protocol).length > 0 && (
              <div className="realtime-panel">
                <h3>üì° Protocol Distribution</h3>
                <div className="protocol-grid">
                  {Object.entries(stats.by_protocol).map(([protocol, count]) => (
                    <div key={protocol} className="protocol-badge">
                      <span className="protocol-icon">{PROTOCOL_ICONS[protocol] || 'üì¶'}</span>
                      <span className="protocol-name">{protocol}</span>
                      <span className="protocol-count">{count}</span>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* Top Functions */}
            {stats.by_function && Object.keys(stats.by_function).length > 0 && (
              <div className="realtime-panel full-width">
                <h3>üî• Top Active Functions</h3>
                <div className="function-list">
                  {Object.entries(stats.by_function).slice(0, 10).map(([func, count], idx) => (
                    <div key={func} className="function-item">
                      <div className="function-rank">#{idx + 1}</div>
                      <div className="function-name">{func}</div>
                      <div className="function-count">{count} calls</div>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>

          {/* Live Events Stream */}
          <div className="realtime-panel full-width">
            <h3>
              üì° Live Event Stream 
              <span className="event-count">({recentEvents.length}/{maxEvents})</span>
            </h3>

            {recentEvents.length === 0 ? (
              <div className="empty-events">
                <p>‚è≥ Waiting for events...</p>
                <p className="hint">Generate some traffic to see live packet flows</p>
              </div>
            ) : (
              <div className="events-stream">
                {recentEvents.map((event, idx) => (
                  <div key={idx} className="event-item" style={{ animationDelay: `${idx * 0.02}s` }}>
                    <div className="event-time">{formatTimestamp(event.timestamp)}</div>

                    {event.hook && (
                      <div 
                        className="event-hook"
                        style={{ background: HOOK_COLORS[event.hook] || '#667eea' }}
                      >
                        {event.hook}
                      </div>
                    )}

                    <div className="event-function">{event.function || 'unknown'}</div>

                    {event.protocol && (
                      <div className="event-protocol">
                        {PROTOCOL_ICONS[event.protocol] || 'üì¶'} {event.protocol}
                      </div>
                    )}

                    {event.src_ip && event.dst_ip && (
                      <div className="event-flow">
                        <span className="event-ip">{event.src_ip}</span>
                        <span className="event-arrow">‚Üí</span>
                        <span className="event-ip">{event.dst_ip}</span>
                      </div>
                    )}

                    {event.verdict && (
                      <div 
                        className="event-verdict"
                        style={{ background: VERDICT_COLORS[event.verdict] || '#6c757d' }}
                      >
                        {event.verdict}
                      </div>
                    )}
                  </div>
                ))}
              </div>
            )}
          </div>
        </>
      )}
    </div>
  );
}

export default RealtimeView;
