import React, { useState, useEffect } from 'react';
import axios from 'axios';
import RealtimeView from './RealtimeView';
import './App.css';

const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:5000/api';

function App() {
  // State
  const [sessions, setSessions] = useState([]);
  const [files, setFiles] = useState([]);
  const [modes, setModes] = useState([]);
  const [functions, setFunctions] = useState(null);
  const [newSession, setNewSession] = useState({
    id: '',
    mode: 'full',
    filter: '',
    maxFunctions: 30
  });
  const [selectedSession, setSelectedSession] = useState(null);
  const [sessionStats, setSessionStats] = useState(null);
  const [health, setHealth] = useState(null);
  const [loading, setLoading] = useState(false);
  const [discovering, setDiscovering] = useState(false);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('sessions'); // sessions, realtime, discovery, files

  // Load initial data
  useEffect(() => {
    checkHealth();
    loadModes();
    loadFunctions();
    loadSessions();
    loadFiles();
  }, []);

  // Poll active sessions
  useEffect(() => {
    const interval = setInterval(() => {
      if (sessions.length > 0) {
        loadSessions();
        if (selectedSession) {
          loadSessionStats(selectedSession);
        }
      }
    }, 1000);

    return () => clearInterval(interval);
  }, [sessions, selectedSession]);

  // API Calls
  const checkHealth = async () => {
    try {
      const res = await axios.get(`${API_BASE}/health`);
      setHealth(res.data);
    } catch (err) {
      setError('Backend khÃ´ng kháº£ dá»¥ng. Vui lÃ²ng kiá»ƒm tra server!');
    }
  };

  const loadModes = async () => {
    try {
      const res = await axios.get(`${API_BASE}/modes`);
      setModes(res.data.modes);
    } catch (err) {
      console.error('Error loading modes:', err);
    }
  };

  const loadFunctions = async () => {
    try {
      const res = await axios.get(`${API_BASE}/functions`);
      setFunctions(res.data);
    } catch (err) {
      console.log('Functions not discovered yet');
    }
  };

  const discoverFunctions = async () => {
    setDiscovering(true);
    setError(null);

    try {
      const res = await axios.post(`${API_BASE}/discover`, {
        max_priority: 2,
        categories: null
      });

      setFunctions(null);
      await loadFunctions();
      setError(null);
      alert(`âœ“ ÄÃ£ phÃ¡t hiá»‡n ${res.data.total_discovered} functions!`);
    } catch (err) {
      setError(err.response?.data?.error || 'Lá»—i khi discover functions');
    } finally {
      setDiscovering(false);
    }
  };

  const loadSessions = async () => {
    try {
      const res = await axios.get(`${API_BASE}/sessions`);
      setSessions(res.data.sessions);
    } catch (err) {
      console.error('Error loading sessions:', err);
    }
  };

  const loadSessionStats = async (sessionId) => {
    try {
      const res = await axios.get(`${API_BASE}/sessions/${sessionId}/stats`);
      setSessionStats(res.data);
    } catch (err) {
      console.error('Error loading stats:', err);
    }
  };

  const loadFiles = async () => {
    try {
      const res = await axios.get(`${API_BASE}/files`);
      setFiles(res.data.files);
    } catch (err) {
      console.error('Error loading files:', err);
    }
  };

  const startSession = async () => {
    setLoading(true);
    setError(null);

    try {
      const sessionId = newSession.id || `trace_${Date.now()}`;
      await axios.post(`${API_BASE}/sessions`, {
        session_id: sessionId,
        mode: newSession.mode,
        pcap_filter: newSession.filter,
        max_functions: newSession.maxFunctions
      });

      setNewSession({ id: '', mode: 'full', filter: '', maxFunctions: 30 });
      loadSessions();
      setActiveTab('sessions');
    } catch (err) {
